mapper.select:

SELECT item_code, item_name, spec_name, discount, applied_price 
FROM (
    SELECT ROWNUM rn, item_code, item_name, spec_name, discount, applied_price 
    FROM (
        SELECT item_code, item_name, spec_name, discount, applied_price 
        FROM item_price
        WHERE





        ROWNUM <= #{pageNum} * #{amount}
        ORDER BY item_code
    )
)
WHERE rn > (#{pageNum} - 1) * #{amount}
